(function ($) {
    "use strict";
    function kcg_el_svg_init() {

        $('.svg').each(elstartClass);

    }

    function elstartClass() {

        var $icon_svg = $(this);
        if ($icon_svg.hasClass('svg-inline')) return true;

        var bg = $icon_svg.css('background-image').replace('url(', '').replace(')', '').replace(/\"/g, '');

        if (bg && bg != 'none') $.get(bg, function (resp) {
            $icon_svg.html($(resp).find('svg')).addClass('svg-inline').trigger('svg-ready');
        });
    }
    kcg_el_svg_init();
})(jQuery);