(function ($) {
	"use strict";
	// Clear cache files
	$(document).ready(function(){
		
		if($("#wpadminbar .kcgs-purge-clear").length > 0){
			//console.log('ok');
			var ids="kcgs-purge-clear";
			var _action = "kcgs_purge_current_clear";
			
			$('.'+ ids + ' .ab-submenu a').on("click", function(e) {
				e.preventDefault();				
					var $this = $(this);
					var href= $this.attr('href');
					href = href.replace('#clear-','');
					
					if(href=='kcgs-all'){
						var confirmation = confirm("Are you sure want to remove all cache files?");
					}else{
						var confirmation = true;
					}
					if(href && confirmation){
						$.ajax({
							url: kcgs_ajax_url,
							type: "post",
							data: {
								action: _action,
								security: kcgs_nonce,
								kcgs_name : href
							},
							success: function(response) {
								if(response){
									setTimeout(function() {
										location.reload(true);
									}, 50);
								}
							},
						});
					}
			});
		}
	});
	
})(window.jQuery);
