( function( $ ) {
	"use strict";
	var WidgetTabHandler = function($scope, t, e) {

		var $selector = "#" + $scope.find(".kcg-script").attr("id").toString();
		t($selector + " ul > li").click(function () {
          var e = t(this).index(),
              a = t(this).closest(".kcg-script"),
              n = t(a).children(".kcg-tabs").children("ul").children("li"),
              i = t(a).children(".kcg-tabcontent").children("div");
          t(this).parent("li").addClass("active"),
              t(n).removeClass("active").addClass("inactive"),
              t(this).addClass("active").removeClass("inactive"),
              t(i).removeClass("active").addClass("inactive"),
              t(i).eq(e).addClass("active").removeClass("inactive");
      }); 

	};
	$(window).on('elementor/frontend/init', function () {
		elementorFrontend.hooks.addAction('frontend/element_ready/kcg-tab.default', WidgetTabHandler);
	});
})(jQuery);